/**
 * @param string $input
 * @param int|null $until
 * @return int
 * @throws \Exception
 */
function processInput(string $input, int $until = null) : int
{
    $floor = 0;
    $count = 0;
    $input = str_split($input);

    foreach ($input as $item => $value) {
        $floor += decodeFloor($value);
        $count++;
        if(is_int($until) && $until === $floor) {
            return $count;
        }
    }

    return $floor;
}


function decodeFloor(string $value) : int
{
    $valid = ['(', ')'];
    if (!in_array($value, $valid)) {
        throw new \Exception($value . ' is neither ( nor )');
    }
    if ($value === '(') {
        return 1;
    }
    return -1;
}

/**
 * Tests
 */
assert(decodeFloor('(') === 1);
assert(decodeFloor(')') === -1);
assert(processInput('((((((((()))))))))') === 0);
assert(processInput('(((((((((') === 9);

assert(processInput('((())))', -1) === 7);

